mapper.select:
SELECT
${field}
FROM article WHERE article_type = #{article_type} AND isdeleted = 0 ORDER BY article_releaseTime DESC LIMIT #{startIndex}, #{pageScale}

SELECT
${field}
FROM article WHERE article_type = #{article_type} AND isdeleted = 0 AND
article_title LIKE CONCAT('%',#{word},'%') or article_author LIKE CONCAT('%',#{word},'%') or article_label LIKE CONCAT('%',#{word},'%')
ORDER BY article_releaseTime DESC LIMIT #{startIndex}, #{pageScale}

SELECT
${field}
FROM article WHERE article_type = #{article_type} AND article_releaseTime > #{beginDate} AND #{endDate} >= article_releaseTime AND isdeleted = 0 ORDER BY article_releaseTime DESC LIMIT #{startIndex}, #{pageScale}

SELECT
${field}
FROM article WHERE article_type = #{article_type} AND article_label = #{article_label} AND isdeleted = 0 ORDER BY article_releaseTime DESC LIMIT #{startIndex}, #{pageScale}
